List<CronTrigger> crons = [SELECT Id FROM CronTrigger WHERE State IN ('WAITING', 'ACQUIRED', 'EXECUTING', 'PAUSED', 'BLOCKED', 'PAUSED_BLOCKED')];
for(CronTrigger cron : crons) {
    System.abortJob(cron.Id);
}

